INSERT INTO ponies_character (id, name, description, image) VALUES (3,'Fluttershy','Fluttershy is a female Pegasus pony and one of the main characters of My Little Pony Friendship is Magic. She lives in a small cottage near the Everfree Forest and takes care of animals, the most prominent of her charges being Angel the bunny. She represents the element of kindness.','https://static.wikia.nocookie.net/mlp/images/d/d6/Fluttershy_ID_S1E17.png/revision/latest/scale-to-width-down/225?cb=20190410214903'); 
