INSERT INTO ponies_character (id, name, description, image) VALUES (91,'Wallflower Blush','Wallflower Blush, or simply Wallflower, is a female human who appears as the main antagonist in the Equestria Girls hour-long special Forgotten Friendship and its novelization A Friendship to Remember. She is a student at Canterlot High School and the founder of its gardening club.','https://static.wikia.nocookie.net/mlp/images/e/e4/Wallflower_Blush_ID_EGFF.png/revision/latest/scale-to-width-down/160?cb=20181028073826'); 
