INSERT INTO ponies_character (id, name, description, image) VALUES (93,'Vignette Valencia','Vignette Valencia is a female human who appears as the main antagonist in the Equestria Girls hour-long special Rollercoaster of Friendship. She is a social media celebrity in charge of the grand opening of the Equestria Land theme park.','https://static.wikia.nocookie.net/mlp/images/6/6b/Vignette_Valencia_ID_EGROF.png/revision/latest/scale-to-width-down/250?cb=20180708224539'); 
