INSERT INTO ponies_character (id, name, description, image) VALUES (94,'Kiwi Lollipop and Supernova Zap','Kiwi Lollipop, also known as K-Lo, and Supernova Zap, also known as Su-Z, are two female human characters who appear as the antagonists of the Equestria Girls hour-long special Sunset\'s Backstage Pass. They are a pop music duo under the name PostCrush.','https://static.wikia.nocookie.net/mlp/images/6/6c/Kiwi_Lollipop_ID_EGSBP.png/revision/latest/scale-to-width-down/150?cb=20200111055936'); 
