INSERT INTO ponies_character (id, name, description, image) VALUES (97,'Flim and Flam','Flim and Flam, collectively known as the Flim Flam Brothers, are twin salespony unicorn brothers who first appear as antagonists in The Super Speedy Cider Squeezy 6000 and return in later episodes, animated shorts, IDW comics, and chapter books. Flim is called Flim Skim in some merchandise.','https://static.wikia.nocookie.net/mlp/images/8/81/Flim_singing_on_podium_ID_S2E15.png/revision/latest/scale-to-width-down/225?cb=20120201023343'); 
