INSERT INTO ponies_character (id, name, description, image) VALUES (99,'Mane-iac','The Mane-iac is a female Earth pony super-villain from Maretropolis who first appears in the season four episode Power Ponies. She is the archenemy of the comic book superhero team, the Power Ponies. She is called Mane-iac Mayhem in some merchandise.','https://static.wikia.nocookie.net/mlp/images/6/6e/Mane-iac_ID_S4E06.png/revision/latest/scale-to-width-down/225?cb=20210912015302'); 
