INSERT INTO ponies_character (id, name, description, image) VALUES (115,'Coloratura','Coloratura, nicknamed Rara and stage-named Countess Coloratura, is a female Earth pony and supporting character who appears in the season five episode The Mane Attraction and the season nine episode The Last Problem. She is a famous pop singer in Equestria and a childhood friend of Applejack\'s.','https://static.wikia.nocookie.net/mlp/images/4/4f/Coloratura_ID_S5E24.png/revision/latest/scale-to-width-down/200?cb=20160331020344'); 
