INSERT INTO ponies_character (id, name, description, image) VALUES (117,'Songbird Serenade','Songbird Serenade is a female Pegasus pony who appears as a supporting character in My Little Pony The Movie, a central character in the chapter book Beyond Equestria: Pinkie Pie Steps Up, and in the IDW comics\' My Little Pony: Ponyville Mysteries Issue #5. She is the biggest pop star in Equestria and the headlining act for the Friendship Festival.','https://static.wikia.nocookie.net/mlp/images/3/31/Songbird_Serenade_ID_MLPTM.png/revision/latest/scale-to-width-down/180?cb=20171013144042'); 
