INSERT INTO ponies_character (id, name, description, image) VALUES (128,'Nurse Redheart','Nurse Redheart is a female Earth pony who works as a nurse at Ponyville Hospital. She has a white coat, pink mane and tail, blue eyes, and a varying cutie mark at least of a medical cross with a heart in each corner. She wears a nurse\'s cap that bears an emblem with the same design as her cutie mark.','https://static.wikia.nocookie.net/mlp/images/5/5d/Nurse_Redheart_ID_S7E3.png/revision/latest/scale-to-width-down/240?cb=20170503175548'); 
