INSERT INTO ponies_character (id, name, description, image) VALUES (132,'Nurse ponies','Nurse ponies are ponies who work as nurses at Ponyville Hospital and appear in Applebuck Season, Read It and Weep, Where the Apple Lies, and other episodes. Most are unnamed in the show, "Nurse Sweetheart", "Nurse Tenderheart", and "Nursery Rhyme" being placeholder names, but Nurse Snowheart is named in merchandise and other media.','https://static.wikia.nocookie.net/mlp/images/2/2d/Full_view_of_Ponyville_Hospital_surgery_theater_S6E23.png/revision/latest/scale-to-width-down/250?cb=20161009173132'); 
