INSERT INTO ponies_character (id, name, description, image) VALUES (149,'Fancy Pants\' entourage','Swan Song, Golden Gavel, and Silver Frames are three ponies who first appear in Sweet and Elite. They are unnamed in the show, but they are named in merchandise, and Swan Song is also named in another depiction.','https://static.wikia.nocookie.net/mlp/images/3/3e/Trio_shocked_S02E09.png/revision/latest/scale-to-width-down/250?cb=20121204173749'); 
