INSERT INTO ponies_character (id, name, description, image) VALUES (150,'Fleur de Lis','Fleur de Lis, or Fleur Dis Lee, is a female unicorn supermodel who first appears in the season two episode Sweet and Elite. She is unnamed in both the show and the comics, but she is named "Fleur de Lis" in first-party merchandise and "Fleur Dis Lee" in other merchandise and depictions. She is typically accompanied by Fancy Pants.','https://static.wikia.nocookie.net/mlp/images/2/20/Fleur_Dis_Lee_startled_S2E09.png/revision/latest/scale-to-width-down/240?cb=20151014110946'); 
