INSERT INTO ponies_character (id, name, description, image) VALUES (153,'Cherry Jubilee','Cherry Jubilee is a female Earth pony who first appears in the season two episode The Last Roundup. She is the owner and boss of Cherry Hill Ranch in Dodge Junction. She is called Cherries Jubilant in some merchandise.','https://static.wikia.nocookie.net/mlp/images/1/13/Cherry_Jubilee_id_S02E14.png/revision/latest/scale-to-width-down/200?cb=20121205061839'); 
