INSERT INTO ponies_character (id, name, description, image) VALUES (157,'Flitter','Flitter is a grayish blue Pegasus pony who wears a pink bow in her mane; she has a slightly slurred accent. Her cutie mark is three blue and white dragonflies, although the number of dragonflies changes sometimes.[specify]','https://static.wikia.nocookie.net/mlp/images/5/5b/Flitter_standing_CROPPED.png/revision/latest/scale-to-width-down/240?cb=20130528124239'); 
