INSERT INTO ponies_character (id, name, description, image) VALUES (159,'Bulk Biceps','Bulk Biceps is a male Pegasus pony who first appears in the episode Hurricane Fluttershy. He has a white coat, a dirty-blond mane, and intense red eyes. He has an extraordinarily muscular character model.','https://static.wikia.nocookie.net/mlp/images/2/29/Bulk_Biceps_ID_S4E05.png/revision/latest/scale-to-width-down/250?cb=20131216183904'); 
