INSERT INTO ponies_character (id, name, description, image) VALUES (165,'The Pony Tones','The Pony Tones, or Ponytones, are a vocal quintet who appear in Filli Vanilli. The group is initially a quartet consisting of Rarity (soprano), Big McIntosh (bass), Toe-Tapper (tenor), and Torch Song (alto); Fluttershy later joins the group as a part-time fifth member.','https://static.wikia.nocookie.net/mlp/images/d/d7/Fluttershy_and_Ponytones_%22got_the_music%22_2_S4E14.png/revision/latest/scale-to-width-down/225?cb=20140217173628'); 
