INSERT INTO ponies_character (id, name, description, image) VALUES (166,'Randolph','Randolph is an elderly male Earth pony who appears in Twilight Time, Crusaders of the Lost Mark, and The Cart Before the Ponies as a butler for Diamond Tiara\'s family. He has a pale blue coat, white mane and tail, blue eyes, and a cutie mark of a feather duster.','https://static.wikia.nocookie.net/mlp/images/8/8f/Randolph_ID_S4E15.png/revision/latest/scale-to-width-down/200?cb=20140226190105'); 
