INSERT INTO ponies_character (id, name, description, image) VALUES (168,'Stellar Eclipse','Stellar Eclipse is a male Pegasus who appears in Trade Ya! He has a milk chocolate brown coat, black and purple striped mane and tail, purple eyes, and a cutie mark of usually a sun symbol consisting of a pink circle surrounded by eight triangles; four large, purple ones, and four small, light pink ones -- in one shot, his cutie mark appears as an hourglass. He walks with a wheelchair cart.','https://static.wikia.nocookie.net/mlp/images/6/63/Stellar_Eclipse_ID_S4E22.png/revision/latest/scale-to-width-down/240?cb=20140421170028'); 
